mapper.update:
update pet set
    pet_name = #{pet_name}
    ,pet_gender = #{pet_gender}
    ,pet_kind  = #{pet_kind}
    ,pet_type = #{pet_type}
    ,pet_age  = #{pet_age}
    ,pet_size = #{pet_size}
    ,pet_total = #{pet_total}
    ,pet_intro = #{pet_intro }
    ,pet_vaccin = #{pet_vaccin}
    ,pet_toilet = #{pet_toilet})
where
    member_id = #{member_id}
